class Home extends React.Component {
  state = {
    queryError: "",
    selected: [],
  };

  fectchData = async function () {
    this.setState({ isLoading: true });
    let endpoint = "https://randomuser.me/api";

    try {
      let response = await fetch(endpoint);

      if (response.ok) {
        console.log("Response is ok!!");
        let data = await response.json();
        console.log(data);
        if (data) {
          this.setState({ selected: data.results });
          console.log(this.state.selected);
        } else {
          this.setState({ queryError: data.Error });
          console.log(this.state.queryError);
        }
      } else {
        alert("Cant fetch the data!");
      }
    } catch (error) {
      alert(error);
    }
  };

  componentDidMount = async function () {
    this.fectchData();
  };



  render() {
    return <>
     {this.state.selected.map((users) => {
              return (
                <div>
                  <p>{users.name.first}</p>
                  <Profiles user={this.state.selcted}/>
                </div>
              );
            })}
    </>;
  }
}

