class Home extends React.Component {
  state = {
    queryError: "",
    selected: [],
  };

  fectchData = async function () {
    this.setState({ isLoading: true });
    let endpoint = "https://randomuser.me/api";

    try {
      let response = await fetch(endpoint);

      if (response.ok) {
        console.log("Response is ok!!");
        let data = await response.json();
        console.log(data);
        if (data) {
          this.setState({ selected: data });
          console.log(this.state.selected);
        } else {
          this.setState({ queryError: data.Error });
          console.log(this.state.queryError);
        }
      } else {
        alert("Cant fetch the data!");
      }
    } catch (error) {
      alert(error);
    }
  };

  componentDidMount = async function () {
    this.fectchData();
  };



  render() {
    return <>
     {this.state.selected.resultsmap((film) => {
              return (
                <div
                  key={film.show.id}
                  className="col-md-2 m-1  "
                  onClick={() =>
                    this.props.history.push("/details/" + film.show.id)
                  }
                  onDoubleClick
                >
                  <span class="text-truncate">{film.show.name}</span>
                  {!this.props.loading && film.show.image != null ? (
                    <img
                      height="250px"
                      alt={film.title}
                      className="d-block w-80 "
                      src={film.show.image.medium}
                    />
                  ) : (
                    <img
                      height="250px"
                      alt={film.title}
                      className="d-block w-80"
                      src={"https://via.placeholder.com/150"}
                    />
                  )}

                  <p>{film.show.genres[0]} - {film.show.ended}</p>
                </div>
              );
            })}
    </>;
  }
}

export default Home;
