import { React, useState } from "react";
import { Container, Row, Col, Image } from "react-bootstrap";
import { HiChevronDoubleDown } from "react-icons/hi";
import { RiLockPasswordFill } from "react-icons/ri";
import { AiFillPhone, AiOutlineUser, AiTwotoneMail } from "react-icons/ai";
import { FaAddressBook } from "react-icons/fa";
import Datetime from "react-datetime";

const ProfileFunctional = ({ data }) => {
  const [show, setShow] = useState("");
  const [field, setField] = useState("");

  return (
    <div>
      {data.map((users) => (
        <Container key={users.login.uuid}>
          {users.picture != null ? (
            <Row>
              <Col xs={6} md={4}>
                <Image src={users.picture.large} roundedCircle />
              </Col>
              <Row>
              <p>{field}</p>
              <h3>{show}</h3>

              </Row>

            </Row>
          ) : (
            <Row className>
              <Col xs={6} md={4}>
                <Image src="holder.js/171x180" roundedCircle />
              </Col>
            </Row>
          )}
          <Row>
            <Col>
              <RiLockPasswordFill
                onMouseOver={() => {
                  setShow(users.login.password);
                  setField("My Password is");

                }}
                size={60}
              />
            </Col>
            <Col>
              <AiFillPhone
                onMouseOver={() => {
                  setShow(users.phone);
                  setField("My phone number is");
                }}
                size={60}
              />
            </Col>
            <Col>
              <FaAddressBook
                onMouseOver={() => {
                  setShow(
                    users.location.street.name +
                      " " +
                      users.location.street.number
                  );
                  setField("My address is");
                }}
                size={60}
              />
            </Col>
            <Col>
              {/* <HiChevronDoubleDown
                onMouseOver={() => this.setState({ show: "no", field: "My birthday is" })}
              /> */}
            </Col>
            <Col>
              <AiOutlineUser
                onMouseOver={() => {
                  setShow(
                    users.name.title +
                      " " +
                      users.name.first +
                      " " +
                      users.name.last
                  );
                  setField("My name is");
                }}
                size={60}
              />
            </Col>
            <Col>
              <AiTwotoneMail
                onMouseOver={() => {
                  setShow(users.email);
                  setField("My email is");
                }}
                size={60}
              />
            </Col>
          </Row>
        </Container>
      ))}
    </div>
  );
};

export default ProfileFunctional;
