import {React, useState} from "react";
import { Container, Row, Col, Image } from "react-bootstrap";
import { HiChevronDoubleDown } from "react-icons/hi";
import {RiLockPasswordFill} from "react-icons/ri"
import {AiFillPhone} from "react-icons/ai"
import {FaAddressBook} from "react-icons/fa"
import Datetime from 'react-datetime';

const ProfileFunctional = ({data}) =>{
const [show, setShow] = useState("");
const [field, setField] = useState("");

return (
    <div>
      {data.map((users) => (
        <Container key={users.login.uuid}>
          {users.picture != null ? (
            <Row>
              <Col xs={6} md={4}>
                <Image src={users.picture.large} roundedCircle />
              </Col>
              <p>{field}</p>
              <h3>{show}</h3>
            </Row>
          ) : (
            <Row>
              <Col xs={6} md={4}>
                <Image src="holder.js/171x180" roundedCircle />
              </Col>
            </Row>
          )}
          <Row>
            <Col>
              <RiLockPasswordFill
                onMouseOver={() =>
                  {setShow(users.login.password)
                  setField("My Password is")}

                }
              />
            </Col>
            <Col>
              <AiFillPhone
                onMouseOver={() =>
                    {setShow(users.phone)
                        setField("My phone number is")}
                }
              />
            </Col>
            <Col>
              <FaAddressBook
                onMouseOver={() =>  {setShow(users.location.street.name + " " + users.location.street.number)
                    setField("My address is")}}
              />
            </Col>
            <Col>
              <HiChevronDoubleDown
                onMouseOver={() => setState({ show: "no", field: "My birthday is" })}
              />
            </Col>
            <Col>
              <HiChevronDoubleDown
                onMouseOver={() => {setShow(users.name.title + " " + users.name.first + " " + users.name.last) 
                setField( "My name is")}}
              />
            </Col>
            <Col>
              <HiChevronDoubleDown
                onMouseOver={() => this.setState({ show: users.email, field: "My email is" })}
              />
            </Col>
          </Row>
        </Container>
      ))}
    </div>
  );


}

export default ProfileFunctional;
