import React from "react";
import axios from "axios";
import Profiles from "./Profiles";
import {Row, Div, Col} from "react-bootstrap"
import { HiChevronDoubleDown} from 'react-icons/hi';

class Home extends React.Component {
  state = {
    queryError: "",
    selected: [],
    show: ""

  };

  fectchData = async function () {
    this.setState({ isLoading: true });
    let endpoint = "https://randomuser.me/api";

    try {
      let response = await fetch(endpoint);
      if (response.ok) {
        console.log("Response is ok!!");
        let data = await response.json();
        console.log(data);
        if (data) {
          this.setState({ selected: data.results });
          console.log(this.state.selected);
        } else {
          this.setState({ queryError: data.Error });
          console.log(this.state.queryError);
        }
      } else {
        alert("Cant fetch the data!");
      }
    } catch (error) {
      alert(error);
    }
  };

  componentDidMount = async function () {
    this.fectchData();
  };

  render() {
    return (
      <>
        <Profiles user={this.state.selected} />
        <p>{this.state.show}</p>
        <Row>
          <Col>
        <HiChevronDoubleDown onMouseOver={()=>this.setState({show: "My Passowrd is"})}/>
        </Col>
        <Col>
        <HiChevronDoubleDown onMouseOver={()=>this.setState({show: "My Passowrd rris"})}/>
        </Col>
        </Row>

      </>
    );
  }
}

export default Home;
